import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import * as actionTypes from '../../redux/actionTypes'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button } from 'reactstrap'
import { Flex } from '../../components/shared/Flex/Flex'
import { waitForData } from '../../redux/actions'

export const Main = () => {
  const dispatch = useDispatch()
  const [regionState, setRegionState] = useState('')
  const [regionCapitalState, setRegionCapitalState] = useState([])

  const inputChangeHandler = event => {
    setRegionState({
      ...regionState,
      region: event.target.value
    })
  }

  const formSubmitHandler = async event => {
    event.preventDefault()
    const regionData = await fetch(
      `https://restcountries.eu/rest/v2/region/${regionState.region}`
    )
    if (regionData.status === 200) {
      const newRegionData = await regionData.json()
      let result = Promise.all(getCapitalsData(newRegionData))
      console.log('sitas', result)
      // setRegionCapitalState(
      //   ...regionCapitalState,
      //   getCapitalsData(newRegionData)
      // )
    } else {
      alert('please enter a valid region etc. Europe, Asia, Africa...')
    }
  }

  const getCapitalsData = newRegionData => {
    const citiesData = newRegionData.map(city => {
      return city.capital
      // let capitalData = await waitForData(cityName.capital)
      // if (capitalData.cod !== '404') {
      //   console.log(capitalData)
      //   let WeatherData = await capitalData
      //   return WeatherData
      // }
    })
    return waitForData(citiesData)
  }

  return (
    <>
      <Flex justify='center' align='center'>
        <input type='text' onChange={inputChangeHandler}></input>
        <Button color='secondary' onClick={formSubmitHandler}>
          Search Region
        </Button>
      </Flex>
      <Flex></Flex>
    </>
  )
}
