import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import * as actionTypes from '../../redux/actionTypes'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button } from 'reactstrap'
import { Flex } from '../../components/shared/Flex/Flex'
import { waitForData } from '../../redux/actions'

export const Main = () => {
  const dispatch = useDispatch()
  const [regionState, setRegionState] = useState('')
  const [regionCapitalState, setRegionCapitalState] = useState([])

  const inputChangeHandler = event => {
    setRegionState({
      ...regionState,
      region: event.target.value
    })
  }

  const formSubmitHandler = async event => {
    event.preventDefault()
    const regionData = await fetch(
      `https://restcountries.eu/rest/v2/region/${regionState.region}`
    )
    if (regionData.status === 200) {
      const newRegionData = await regionData.json()
      const regionCapitals = newRegionData.map(async cityName => {
        let capitalNames = await waitForData(cityName.capital)
        let result = await capitalNames
        console.log(result)
        return result
      })
      console.log(regionCapitals)
      // setRegionCapitalState({ ...regionCapitalState, allCapitals })
      // console.log(regionCapitalState)
    } else {
      alert('please enter a valid region etc. Europe, Asia, Africa...')
    }
  }

  return (
    <>
      <Flex justify='center' align='center'>
        <input type='text' onChange={inputChangeHandler}></input>
        <Button color='secondary' onClick={formSubmitHandler}>
          Search Region
        </Button>
      </Flex>
      <Flex></Flex>
    </>
  )
}
