import React, { useState } from 'react'
import { Form, FormGroup, Label, Input, Button, Col } from 'reactstrap'

export const Register = () => {
  const [formState, setFormState] = useState({
    username: '',
    password: '',
    rPassword: ''
  })

  const usernameInputHandler = event => {
    setFormState({
      ...formState,
      username: event.target.value
    })
  }
  const passwordInputHandler = event => {
    setFormState({
      ...formState,
      password: event.target.value
    })
  }
  const rPasswordInputHandler = event => {
    setFormState({
      ...formState,
      rPassword: event.target.value
    })
  }

  const registerUser = () => {
    let newUser = {
      username: formState.username,
      password: formState.password,
      rPassword: formState.rPassword
    }

    console.log(newUser)

    //console.log(email, password, rPassword);
    // if (password === rPassword) {
    //   let body = {
    //     username: username,
    //     password: password
    //   }
    //   fetch('http://localhost:4000/api/v1/user/register', {
    //     method: 'POST',
    //     body: JSON.stringify(body),
    //     headers: {
    //       'Content-Type': 'application/json'
    //     }
    //   })
    //     .then(json => {
    //       if (json.status == 200) {
    //         return json.json()
    //       } else {
    //         alert('REGISTRATION: failed')
    //       }
    //     })
    //     .then(response => {
    //       if (response) {
    //         alert('REGISTRATION: success')
    //         // window.location.href = "/login";
    //       }
    //     })
    //     .catch(e => {
    //       console.log(e)
    //     })
    // } else {
    //   alert('REGISTRATION: Password does not match')
    // }
  }

  return (
    <Form>
      <FormGroup>
        <Label for='exampleEmail'>Welcome to brand new WeatherApp!</Label>
        <Input plaintext value='Register new User' />
      </FormGroup>
      <FormGroup row='true'>
        <Label sm={2} size='lg' for='exampleEmail'>
          Username
        </Label>
        <Col sm={10}>
          <Input
            bsSize='lg'
            type='username'
            name='username'
            id='exampleUsername'
            placeholder='Enter Username'
            value={formState.username}
            onChange={usernameInputHandler}
          />
        </Col>
      </FormGroup>
      <FormGroup row='true'>
        <Label sm={2} size='lg' for='examplePassword'>
          Password
        </Label>
        <Col sm={10}>
          <Input
            bsSize='lg'
            type='password'
            name='password'
            id='examplePassword'
            placeholder='Enter Password'
            value={formState.password}
            onChange={passwordInputHandler}
          />
        </Col>
      </FormGroup>
      <FormGroup row='true'>
        <Label sm={2} size='lg' for='exampleRPassword'>
          Repeat your Password
        </Label>
        <Col sm={10}>
          <Input
            bsSize='lg'
            type='Rpassword'
            name='Rpassword'
            id='exampleRPassword'
            placeholder='Repeat Password'
            value={formState.rPassword}
            onChange={rPasswordInputHandler}
          />
        </Col>
      </FormGroup>
      <Button row color='primary' onSubmit={registerUser}>
        Register
      </Button>
    </Form>
  )
}
