import React, { useState } from 'react'
import classes from './Nav.module.css'
import { Input } from '../Input/Input'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button } from 'reactstrap'
import { Flex } from '../shared/Flex/Flex'
import { useDispatch } from 'react-redux'
import { useHistory } from 'react-router-dom'
import * as actionTypes from '../../redux/actionTypes'

export const Nav = () => {
  const dispatch = useDispatch()
  const history = useHistory()
  const [regionState, setRegionState] = useState('')

  const logoutHandler = event => {
    event.preventDefault()
    const token = localStorage.getItem('x-auth')

    fetch('http://localhost:4000/api/v1/user/logout', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'x-auth': token
      }
    })
      .then(header => {
        if (!header.ok) {
          throw Error(header)
        }
        return header.json()
      })
      .then(response => {
        localStorage.removeItem('x-auth')
        alert('LOGOUT: successful')
        dispatch({ type: actionTypes.TOGGLE_LOGGED_IN })
        history.push('/login')
      })
      .catch(e => {
        console.log(e)
        alert('LOGOUT: failed!')
      })
  }

  const inputChangeHandler = (formStateKey, event) => {
    console.log(event)
    setRegionState({
      ...regionState,
      labas: event.target.value
    })
  }

  const formSubmitHandler = async event => {
    event.preventDefault()

    const regionData = await fetch(
      `https://restcountries.eu/rest/v2/region/${regionState}`
    )

    if (regionData.status === 201) {
      const newRegionData = await regionData.json()
      console.log(newRegionData)

      const newRegionDataObj = {
        name: newRegionData.name,
        temperature: newRegionData.main.temp,
        icon:
          'http://openweathermap.org/img/w/' +
          newRegionData.weather[0].icon +
          '.png'
      }
      dispatch({ type: 'ADD_REGION_DATA', newRegionDataObj })
    } else {
      alert('please enter a valid region etc. Europe, Asia, Africa...')
    }
  }

  return (
    <Flex
      justify='space-between'
      className={classes.navContainer}
      align='center'
    >
      <Button color='primary' onClick={logoutHandler}>
        Log out
      </Button>
    </Flex>
  )
}
