    //         alert('REGISTRATION: success')
    //         // window.location.href = "/login";
    //       }
    //     })
    //     .catch(e => {
    //       console.log(e)
    //     })
    // } else {
    //   alert('REGISTRATION: Password does not match')
    // }
  }

  return (
    <Form>
      <Container>
        <FormGroup>
          <Label for='exampleEmail'>Welcome to brand new WeatherApp!</Label>
          <Input plaintext value='Register new User' />
        </FormGroup>
        <FormGroup>
          <Label sm={2} size='lg' for='exampleEmail'>
            Username
          </Label>
          <Input
            bsSize='lg'
            type='username'
            name='username'
            id='exampleUsername'
            placeholder='Enter Username'
            value={formState.username}
            onChange={usernameInputHandler}
          />
        </FormGroup>
        <FormGroup>
          <Label sm={2} size='lg' for='examplePassword'>
            Password
          </Label>
          <Input
            bsSize='lg'
            type='password'
            name='password'
            id='examplePassword'
            placeholder='Enter Password'
            value={formState.password}
            onChange={passwordInputHandler}
          />
        </FormGroup>
        <FormGroup>
          <Label sm={4} size='lg' for='exampleRPassword'>
            Repeat your Password
          </Label>
          <Input
            bsSize='lg'
            type='Rpassword'
            name='Rpassword'
            id='exampleRPassword'
            placeholder='Repeat Password'
            value={formState.rPassword}
            onChange={rPasswordInputHandler}
          />
        </FormGroup>
        <Button color='primary' onClick={registerUser}>
          Register
        </Button>
      </Container>
    </Form>
  )
}
