import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button } from 'reactstrap'
import { Flex } from '../../components/shared/Flex/Flex'
import { fetchCityDataByName } from '../../redux/actions/actions'

export const Favorites = () => {
  const dispatch = useDispatch()
  const [formState, setFormState] = useState('')
  // const [citiesState, setCitiesState] = useState([])

  const inputChangeHandler = event => {
    setFormState({
      ...formState,
      city: event.target.value
    })
  }

  const formSubmitHandler = async event => {
    event.preventDefault()
    const cityData = await fetchCityDataByName(formState.city)
    addCityForUser(cityData)
  }

  const addCityForUser = cityData => {
    let token = localStorage.getItem('x-auth')

    fetch('http://localhost:4000/api/v1/favorites/addCity', {
      method: 'POST',
      body: JSON.stringify(cityData),
      headers: {
        'x-auth': token,
        'Content-Type': 'application/json'
      }
    })
      .then(header => {
        if (!header.ok) {
          throw Error(header)
        }
        return header
      })
      .then(response => {
        alert('Item added')
      })
      .catch(e => {
        console.log(e)
        alert('Item failed')
      })
  }
  // const addToCartHandler = () => {
  //   const productIndex = cart.findIndex(cartItem => cartItem.id === id)
  //   if (productIndex >= 0) {
  //     // Sitas if'as praeina kai produktas jau yra krepselyje
  //     const newCart = [...cart]
  //     newCart[productIndex].cartQuantity += 1
  //     dispatch({
  //       type: actionTypes.REPLACE_CART,
  //       newCart
  //     })
  //   }
  //   if (productIndex === -1) {
  //     dispatch({
  //       type: actionTypes.ADD_TO_CART,
  //       newCartItem: { ...product, cartQuantity: 1 }
  //     })
  //   }
  // }

  return (
    <Flex justify='center' align='center'>
      <form>
        <input type='text' onChange={inputChangeHandler}></input>
        <Button type='submit' color='secondary' onClick={formSubmitHandler}>
          Add city to favorites
        </Button>
      </form>
    </Flex>
  )
}
