import React, { useState } from 'react'
import {
  Form,
  FormGroup,
  Label,
  Input,
  FormFeedback,
  FormText
} from 'reactstrap'

export const Register = () => {
  const [formState, setFormState] = useState({
    username: '',
    password: '',
    rPassword: ''
  })

  const usernameInputHandler = event => {
    setFormState({
      ...formState,
      username: event.target.value
    })
  }
  const passwordInputHandler = event => {
    setFormState({
      ...formState,
      password: event.target.value
    })
  }
  const rPasswordInputHandler = event => {
    setFormState({
      ...formState,
      rPassword: event.target.value
    })
  }

  const registerUser = () => {
    const username = document.getElementById('registerUsername').value
    const password = document.getElementById('registerPassword').value
    const rPassword = document.getElementById('registerRPassword').value

    //console.log(email, password, rPassword);

    if (password === rPassword) {
      let body = {
        username: username,
        password: password
      }

      fetch('http://localhost:4000/api/v1/user/register', {
        method: 'POST',
        body: JSON.stringify(body),
        headers: {
          'Content-Type': 'application/json'
        }
      })
        .then(json => {
          if (json.status == 200) {
            return json.json()
          } else {
            alert('REGISTRATION: failed')
          }
        })
        .then(response => {
          if (response) {
            alert('REGISTRATION: success')
            // window.location.href = "/login";
          }
        })
        .catch(e => {
          console.log(e)
        })
    } else {
      alert('REGISTRATION: Password does not match')
    }
  }

  return (
    <Form>
      <FormGroup>
        <Label for='exampleEmail'>Welcome to brand new WeatherApp!</Label>
        <Input plaintext value='Register new User' />
      </FormGroup>
      <FormGroup>
        <Label for='exampleEmail'>Username</Label>
        <Input
          type='username'
          name='username'
          id='exampleUsername'
          placeholder='Enter Username'
          value={formState.username}
          onChange={usernameInputHandler}
        />
      </FormGroup>
      <FormGroup>
        <Label for='examplePassword'>Password</Label>
        <Input
          type='password'
          name='password'
          id='examplePassword'
          placeholder='Enter Password'
          value={formState.password}
          onChange={passwordInputHandler}
        />
      </FormGroup>
      <FormGroup>
        <Label for='exampleRPassword'>Repeat your Password</Label>
        <Input
          type='Rpassword'
          name='Rpassword'
          id='exampleRPassword'
          placeholder='Repeat Password'
          value={formState.rPassword}
          onChange={rPasswordInputHandler}
        />
      </FormGroup>
    </Form>
  )
}
