import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import * as actionTypes from '../../redux/actionTypes'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button } from 'reactstrap'
import { Flex } from '../../components/shared/Flex/Flex'

export const Main = () => {
  const dispatch = useDispatch()
  const [regionState, setRegionState] = useState('')

  const inputChangeHandler = event => {
    setRegionState({
      ...regionState,
      region: event.target.value
    })
  }

  const formSubmitHandler = async event => {
    event.preventDefault()

    const regionData = await fetch(
      `https://restcountries.eu/rest/v2/region/${regionState}`
    )

    if (regionData.status === 201) {
      const newRegionData = await regionData.json()
      console.log(newRegionData)

      const newRegionDataObj = {
        name: newRegionData.name,
        temperature: newRegionData.main.temp,
        icon:
          'http://openweathermap.org/img/w/' +
          newRegionData.weather[0].icon +
          '.png'
      }
    } else {
      alert('please enter a valid region etc. Europe, Asia, Africa...')
    }
  }

  return (
    <>
      <Flex justify='center' align='center'>
        <input type='text' onChange={inputChangeHandler}></input>
        <Button color='secondary' onClick={formSubmitHandler}>
          Search Region
        </Button>
      </Flex>
      <Flex></Flex>
    </>
  )
}
