import * as actionTypes from '../actionTypes'

const initialState = { isLoggedIn: true }

export default (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.TOGGLE_LOGGED_IN:
      return { ...state, isLoggedIn: !state.isLoggedIn }
    default:
      return state
  }
}
